ST.xhtml:
THIS^.diSensor		REF=diSensor; 
THIS^.maskOccupied	:= maskOccupied;
THIS^.debounceTime	:= T#30ms;
THIS^.state			:= (THIS^.diSensor = THIS^.maskOccupied);

THIS^.transferCondition	:= transferCondition;
IF (NOT THIS^.transferCondition) THEN
	THIS^.weAreRdy := (NOT THIS^.maskWeAreRdy);
END_IF;

IF (THIS^.transferCondition AND THIS^.machineStateHndl.isMachineInAutomatic()) THEN
	THIS^.weAreRdy := THIS^.maskWeAreRdy;
ELSE
	THIS^.weAreRdy := (NOT THIS^.maskWeAreRdy);
END_IF;		
isTransferActive	  := ((THIS^.weAreRdy = THIS^.maskWeAreRdy) AND (THIS^.maskPartnerIsRdy = THIS^.maskPartnerIsRdy));

THIS^.partnerIsRdy	    REF= partnerIsRdy;
THIS^.weAreRdy		    REF= weAreRdy;
THIS^.machineStateHndl	:= 	 machineStateHndl;
THIS^.transferCondition	:=	 FALSE;
THIS^.maskPartnerIsRdy	:=   maskPartnerIsRdy;
THIS^.maskWeAreRdy		:=	 maskWeAreRdy;

(*recive part*)
IF (THIS^.receiverInterface.readExtCondition2Rec(receiveCondition := THIS^.isEmpty()).isReceiving()) THEN
	THIS^.drive.fastForward();
(*send part*)
ELSIF (THIS^.senderInterface.readExtCondition2Snd(sendCondition := (THIS^.isAligned() OR THIS^.isOccupiedOutfeed())).isSending()) THEN
	THIS^.drive.fastForward();
(*align part*)
ELSIF (THIS^.isOccupiedInfeed() AND THIS^.machineState.isMachineInAutomatic()) THEN
	THIS^.drive.forward();
ELSE
	THIS^.drive.stop();
END_IF

//sensors
THIS^.slowSensor	    := slowSensor; //sensor to slow down
THIS^.stopSensor	    := stopSensor; //sensor to stop
//interfaces
THIS^.senderInterface	:= senderInterface	; //interface to send
THIS^.receiverInterface	:= receiverInterface; //interface to receive
//conveyor drive
THIS^.drive 			:= drive; //drive to run the conveyor
//machine state
THIS^.machineState		:= machineState; //just the machine state

THIS^.cw             REF= cw ;
THIS^.fast           REF= fast ;
THIS^.start          REF= start ;
THIS^.maskFwd      	 :=   maskFwd;

THIS^.cw 	 	:= direction;
THIS^.fast   	:= velocity;
THIS^.start  	:= TRUE;
